import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../Style.css";

const ImageSlider = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const settings = {
    dots: true, // Show navigation dots
    infinite: true, // Infinite looping
    speed: 500, // Transition speed in ms
    slidesToShow: 5, // Number of slides to show at a time
    slidesToScroll: 1, // Number of slides to scroll at a time
    centerMode: true, // Enable center mode
    centerPadding: "10%", // Partial visibility of first and last slides
    autoplay: true, // Enable autoplay
    autoplaySpeed: 3000,
    beforeChange: (current, next) => setActiveSlide(next), // Update active slide index
  };

  const images = [
    "./bn1.png",
    "./bn2.png",
    "./bn3.png",
    "./bn4.png",
    "./bn5.png",
    "./bn6.png",
  ];

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {images.map((image, index) => (
          <div
            key={index}
            className={`slide ${index === activeSlide ? "active" : ""}`}
          >
            <img
              src={image}
              alt={`Slide ${index + 1}`}
              style={{
                width: "300px",
                height: "400px",
                borderRadius: "10px",
                transition: "transform 0.5s ease",
                transform: index === activeSlide ? "scale(1.2)" : "scale(1)",
                opacity: index === activeSlide ? 1 : 0.5,
              }}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default ImageSlider;
